    const key = this.createKey(...args);
    return this.store[key];
  }

  getOrSet(...args: any[]): T {
    const key = this.createKey(...args);
    if (this.store[key]) return this.store[key];
    const item = this.createItem(...args);
    if (this.keys.length >= this.size) {
      const removeKey = this.keys.shift();
      if (removeKey != null) {
