<script lang="ts">
import { defineComponent, reactive } from 'vue';
import { createDatePicker, emits, propsDef } from '../../use/datePicker';
import { omit } from '../../utils/helpers';
import DatePickerBase from './DatePickerBase.vue';
import DatePickerPopover from './DatePickerPopover.vue';

export default defineComponent({
  inheritAttrs: false,
  emits,
  props: propsDef,
  components: { DatePickerBase, DatePickerPopover },
  setup(props, ctx) {
    const datePicker = createDatePicker(props, ctx);
    const slotCtx = reactive(omit(datePicker, 'calendarRef', 'popoverRef'));
    return { ...datePicker, slotCtx };
  },
});
</script>
import type { App } from 'vue';
import * as components from './components/index';
import './styles/index.css';
import { setVueInstance } from './utils/config/index';
import { type Defaults, setupDefaults } from './utils/defaults';

const install = (app: App, defaults: Defaults = {}) => {
  setVueInstance(app);
  app.use(setupDefaults, defaults);
  const prefix = app.config.globalProperties.$VCalendar.componentPrefix;
  for (const componentKey in components) {
    const component = (components as any)[componentKey];
    app.component(`${prefix}${componentKey}`, component);
  }
};

export default { install };
export * from './components';
export { setupDefaults as setupCalendar } from './utils/defaults';
export { popoverDirective } from './utils/popovers';

